INSERT INTO `tuto` VALUES 
(1,'Les √©tapes de la connexion','D√©couvrez les √©tapes de la connexion √† internet','<p>Vous trouverez dans ce tutoriel les √©tapes n√©cessaires de la connexion √† internet.</p><p>Il s‚Äôagit ici d‚Äôun tutoriel tr√®s g√©n√©ral qui vous donnera une vue d‚Äôensemble des √©tapes √† r√©aliser.</p><p><br></p><p><u style=\"color: rgb(0, 61, 165);\">Les pr√©-requis :</u></p><ul><li>Un appareil compatible (smartphone, tablette, ordinateur etc.)</li><li>Un modem, ou routeur d‚Äôacc√®s √† internet (fourni par votre fournisseur d‚Äôacc√®s)</li></ul><p><br></p>',1,'2023-01-04 19:02:57',1,1,0,0),
(2,'L\'utilisation du num√©rique dans la vie courante','D√©couvrez l\'apport du num√©rique au quotidien','<p>L‚Äôutilisation du num√©rique se fait au quotidien de nos jours. Mais √† quoi celui-ci sert-il ? Dans ce tutoriel nous vous aiguillerons sur toutes les possibilit√©s qu‚Äôoffre le num√©rique !</p><p><br></p><p><u style=\"color: rgb(0, 61, 165);\">Les pr√©-requis :</u></p><ul><li>Aucun</li></ul><p><br></p>',2,'2023-01-16 21:11:00',1,1,0,0),
(3,'Le B.A.-BA du smartphone','<p>Apprenez √† conna√Ætre votre smartphone</p>','<p>Ce tutoriel vous guidera √©tape par √©tape pour apprendre √† utiliser les fonctionnalit√©s de base de votre t√©l√©phone, telles que passer et r√©pondre √† des appels, envoyer des messages et acc√©der √† d\`autres applications.</p><p><br></p><p><u style=\"color: rgb(0, 61, 165);\">Les pr√©-requis :</u></p><ul><li>Un smartphone ou une tablette ‚Äúcellulaire‚Äù</li></ul><p><br></p>',12,'2023-01-16 21:18:17',NULL,1,0,0),
(4,'Le B.A.-BA de l‚Äôordinateur','<p>D√©couvrez l\'essentiel de l\'ordinateur</p>','<p>Ce tutoriel vous guidera √©tape par √©tape pour apprendre √† utiliser les fonctionnalit√©s de base de votre ordinateur.</p><p><br></p><p><u style=\"color: rgb(0, 61, 165);\">Les pr√©-requis :</u></p><ul><li>Un ordinateur</li></ul><p><br></p>',3,'2023-01-16 21:21:56',NULL,1,0,0),
(5,'La s√©curit√© et le num√©rique','<p>Parlons \"s√©curit√©\"</p>','<p>La s√©curit√© est importante dans la vie de tous les jours, dans le monde r√©el ou en ligne ! D√©couvrez dans ce tutoriel quelques √©l√©ments sur lesquels il est important d‚Äô√™tre attentif.</p><p><br></p><p><u style=\"color: rgb(0, 61, 165);\">Les pr√©-requis :</u></p><ul><li>Aucun</li></ul><p><br></p>',11,'2023-01-16 21:25:43',NULL,1,0,0),
(6,'Le Pix','<p>D√©couvrez le Pix ! </p>','<p>Le Pix est un site qui vous permet d‚Äô√©valuer votre niveau de ma√Ætrise du num√©rique mais aussi de progresser et de passer des certifications !</p><p><br></p><p><u style=\"color: rgb(0, 61, 165);\">Les pr√©-requis :</u></p><ul><li>ordinateur</li><li>connexion internet</li></ul><p><br></p>',5,'2023-01-16 21:35:05',NULL,1,0,0),(7,'Se connecter √† un wifi public','<p>Comment acc√©der √† un wifi public</p>','<p>Les points d\'acc√®s √† des WIFI publics vous permettent d\'acc√©der √† internet de mani√®re simple,</p><p>et le plus souvent gratuit ! </p><p><br></p><p><u style=\"color: rgb(0, 61, 165);\">Les pr√©-requis :</u></p><ul><li>ordinateur</li><li>smartphone</li><li>tablette</li></ul>',1,'2023-01-16 21:46:14',NULL,1,0,0),(8,'France Connect','<p>France Connect vous permets d\'acc√©der aux diverses applications du service public</p>','<p>Afin d\'acc√©der aux diverses applications du service public, il existe France Connect ! </p><p><br></p><p><u style=\"color: rgb(0, 61, 165);\">Les pr√©-requis : </u></p><ul><li>Une connexion internet</li><li>Un appareil connect√©</li></ul>',1,'2023-01-16 21:55:48',NULL,1,0,0),(9,'Oh non ! Je suis une erreur','<p>Je ne devrais pas √™tre ici</p>','<p>Cette introduction est bien trop courte ...</p>',5,'2023-01-16 22:00:05',NULL,1,0,0);

